/// offset : 0
/// over : false
/// pageCount : 795
/// size : 20
/// total : 15895
/// https://www.wanandroid.com/article/list/0/json
class HomeListData {
  HomeListData({
    this.curPage,
    this.datas,
    this.offset,
    this.over,
    this.pageCount,
    this.size,
    this.total,
  });

  HomeListData.fromJson(dynamic json) {
    curPage = json['curPage'];
    if (json['datas'] != null) {
      datas = [];
      json['datas'].forEach((v) {
        datas?.add(HomeListItemData.fromJson(v));
      });
    }
    offset = json['offset'];
    over = json['over'];
    pageCount = json['pageCount'];
    size = json['size'];
    total = json['total'];
  }

  num? curPage;
  List<HomeListItemData>? datas;
  num? offset;
  bool? over;
  num? pageCount;
  num? size;
  num? total;

  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    map['curPage'] = curPage;
    if (datas != null) {
      map['datas'] = datas?.map((v) => v.toJson()).toList();
    }
    map['offset'] = offset;
    map['over'] = over;
    map['pageCount'] = pageCount;
    map['size'] = size;
    map['total'] = total;
    return map;
  }
}

/// 首页最受欢迎模块 - 问答
// https://wanandroid.com/popular/wenda/json
class HomeWenDaListData {
  List<HomeListItemData>? wendaList;

  HomeWenDaListData.fromJson(dynamic json) {
    if (json is List) {
      wendaList = [];
      for (var element in json) {
        element['type'] = 1; // 修改type属性为1 ，使其置顶
        wendaList?.add(HomeListItemData.fromJson(element));
      }
    }
  }
}

